

let curry = function(fn, prevArgs = []) {
  return function curried(...args) {
    let curArgs = [...prevArgs, ...args];

    if (curArgs.length < fn.length) {
      return curry(fn, curArgs);
    } else {
      return fn(...curArgs);
    }
  };
};

let resolveBabel = curry(function(type, _require, pluginName) {
  return _require.resolve(`babel-${type}-${pluginName}`);
});

let resolvePreset = resolveBabel('preset');
let resolvePlugin = resolveBabel('plugin');

exports.resolveBabel = resolveBabel;
exports.resolvePreset = resolvePreset;
exports.resolvePlugin = resolvePlugin;
